import ReactPlayer from 'react-player';
import './VideoQueue.css';

import React, { Component } from 'react';

export default class VideoQueue extends Component {
	constructor(props) {
		super(props);
		console.log(this.props)
		this.state = {
			videoNames: this.props.videoNames, 
			playing: 0,
			width: '168 px',
			height: '94 px',
			controls: false
		}
	}

	onDragStart = (event, index) => {
		this.draggedItem = this.state.videoNames[index];
		event.dataTransfer.effectAllowed = "move";
		event.dataTransfer.setData("text/html", event.target.parentNode);
		event.dataTransfer.setDragImage(event.target.parentNode, 20, 20);
	}

	onDragOver = (index) => {
		const draggedOverItem = this.state.videoNames[index];

		if(this.draggedItem === draggedOverItem){
			return;
		}

		let items = this.state.videoNames.filter(item => item !== this.draggedItem);

		items.splice(index, 0, this.draggedItem);

		this.setState({videoNames: items})
		console.log(items)
	}

	onDragEnd = () => {
		this.draggedIdx = null
	}

	
	render() {
		const videos = this.props.videoNames.slice(1);
		const videoComp = (videos || []).map((user, i) => {
		return (
		<div className = 'playerContainer'   dragOver = {() => this.props.dragOver(i+1)} draggable>
				<div onDragStart = {(e) => this.props.dragStart(e,i+1) } dragEnd = {this.props.dragEnd}  >
					<ReactPlayer 
				      	url = {JSON.stringify(videos[i].youtube)}
				      	width = {this.state.width}
				      	height= {this.state.height}
				      	playing = {this.state.playing}
				      	controls = {this.state.controls}
				      	onClick={this.props.changeVideo}
				      	/>
				    <button onClick = {()=> {this.props.deleteVideo(this.props.videoNames[i])}}>Delete</button>
				</div>
		</div>)
		})
		return (
			<div>{videoComp}</div>
		);
	}
}


