/* eslint-disable no-unused-expressions */
import React, { Component } from 'react';
import YouTube from 'react-youtube';
import PlayerControls from '../../components/PlayerControls/PlayerControls';

export default class Home extends Component {

	constructor(props) {
		super(props);
		this.state = { bar:0, width: 0, height: 0, time: 0, percentage: 0 , seek: 0, currentTime:0, duration:0};
		}

	componentDidMount(){
		this.updateWindowDimensions();
		window.addEventListener('resize', this.updateWindowDimensions);
		}

	componentWillUnmount(){
		window.removeEventListener('resize',this.updateWindowDimensions);
		}

	updateWindowDimensions = () => {
		this.setState({width: window.innerWidth, height: window.innerHeight});
		}

	_onReady = (event) => {
    	// access to player in all event handlers via event.target
    	const player = event.target;
    	player.pauseVideo();
    	this.videoCounters(event);
		}

	videoDuration = (event) => {
		const player = event.target;
    	this.setState({ time: player.getDuration(), percentage: player.getDuration() });	
		}

	formatTime = (time) => {
		time = Math.round(time);
		var minutes = Math.floor(time/60),
		seconds = time - minutes * 60;

		seconds = seconds < 10 ? '0' + seconds : seconds;

		return minutes + ":" + seconds;
	}

	videoCounters = (event) => {

		const player = event.target;
		console.log(event.getCurrentTime());
		this.setState({ currentTime: player.getCurrentTime(), duration: player.getDuration() });
	}

	initialize = (event) => {
		clearInterval(this.time_update_interval);
		this.updateProgressBar(event);
		this.videoCounters(event);
		const time_update_interval = setInterval( function(){
			this.videoCounters(event);
			this.updateProgressBar(event);
		}, 1000)
	}

	updateProgressBar = (event) => {
		const player=event.target;
		var bar = (player.getCurrentTime()/player.getDuration())*100;
		this.setState({bar:bar})
	}

	render() {
		const opts = {
	      height: this.state.width * 9/16,
	      width: this.state.width,
	      playerVars: { // https://developers.google.com/youtube/player_parameters
	        autoplay: 0,
	        controls: 1
	      }
	    };

	    return (
	      <div>
	      <YouTube
	        videoId="UOxkGD8qRB4"
	        opts={opts}
	        onReady={this._onReady}
	        onStateChange={ this.videoDuration, this.videoCounter, this.updateProgressBar }
	      />
	      <h1>{this.state.duration}</h1>
	      <PlayerControls percentage={this.state.percentage} seek={ this.state.seek } /> 
	      </div>
	    );
	  }

}
