/* eslint-disable no-unused-expressions */
import React, { Component } from 'react';
import YouTube from 'react-youtube';
import PlayerControls from '../../components/PlayerControls/PlayerControls';

export default class Home extends Component {

	constructor(props) {
		super(props);
		this.state = { bar:0, width: 0, height: 0, time: 0, percentage: 0 , seek: 0, currentTime:0, duration:0, isPaused:true};
		}

	componentDidMount(){
		this.updateWindowDimensions();
		window.addEventListener('resize', this.updateWindowDimensions);
		}

	componentWillUnmount(){
		window.removeEventListener('resize',this.updateWindowDimensions);
		}

	updateWindowDimensions = () => {
		this.setState({width: window.innerWidth, height: window.innerHeight});
		}

	_onReady = (event) => {
    	// access to player in all event handlers via event.target
    	const player = event.target;
    	player.pauseVideo();
    	this.setState({ duration: player.getDuration()})

		clearInterval(this.myInterval);
		this.myInterval = setInterval( () => {
			if(!this.state.isPaused){
				this.setState(prevState => ({
				time: prevState.time+1,
				seek: prevState.time+1
					}))
				};

			}, 1000);


		}

	//this is the timer
	barTimer = () => {
		this.setState({isPaused: false})

	}

	_playerPaused = () =>{

			this.setState({isPaused: !this.state.isPaused})

	}

	render() {
		const opts = {
	      height: this.state.width * 9/16, //set the video to a 16:9 ratio based on window size
	      width: this.state.width,
	      playerVars: { // https://developers.google.com/youtube/player_parameters
	        autoplay: 0,
	        controls: 1
	      }
	    };

	    return (
	      <div>
	      <YouTube
	        videoId="UOxkGD8qRB4"
	        opts={opts}
	        onReady={this._onReady}
	        onPlay={this.barTimer}
	        //onPause={this._playerPaused}
	        onStateChange={ this._playerPaused }
	      />
	      <h1>{this.state.time}</h1>
	      <h1>{this.state.isPaused}</h1>
	      <PlayerControls duration={this.state.duration} seek={ this.state.seek } /> 
	      </div>
	    );
	  }

}




