/* eslint-disable no-unused-expressions */
import React, { Component } from 'react';
import YouTube from 'react-youtube';
import PlayerControls from '../../components/PlayerControls/PlayerControls';
import ReactPlayer from 'react-player';
export default class Home extends Component {

	constructor(props) {
		super(props);
		this.state = { 
			bar:0, 
			width: 0, 
			height: 0, 
			time: 0, 
			percentage: 0 , 
			seek: 0, 
			currentTime:0, 
			duration:0, 
			isPaused:true,
			playing: false,
			isseeking: 0,
			played: 0
			};
		}

	componentDidMount(){
		this.updateWindowDimensions();
		window.addEventListener('resize', this.updateWindowDimensions);
		}

	componentWillUnmount(){
		window.removeEventListener('resize',this.updateWindowDimensions);
		}

	updateWindowDimensions = () => {
		this.setState({width: window.innerWidth, height: window.innerHeight});
		}


	timeformat = (seconds) => {
		var seconds = Math.floor(seconds);
		const minutes = Math.floor(seconds/60) ;
		const sseconds = seconds%60 < 10 ? '0'+seconds%60 : seconds%60 ;
		this.setState({percentage: minutes + ':' +sseconds});
		return minutes + ':' +sseconds
	}

	seekChange = (event) => {
		this.setState({seek: event.target.value});
	}

	onTogglePlay = () => {
		this.setState({playing: !this.state.playing})
	}
	onProgress = (event) => {

		if (!this.state.isseeking){
			const time = this.timeformat(event.playedSeconds);
			this.setState({percentage: time, seek: event.playedSeconds})
		}
	}

	isSeeking = (event) => {
		console.log(event.target);
		this.setState({played: event.target.value })
	}

	onDuration = (event) => {

		this.setState({duration: event})
	}

	onSeekChange = (event) => {
		this.setState({played: event.target.value})
	}
	render() {
		const opts = {
	      height: this.state.width * 9/16, //set the video to a 16:9 ratio based on window size
	      width: this.state.width,
	    };

	    return (
	      <div>
	      <h3>React Player</h3>

	      <ReactPlayer 
	      	url = 'https://www.youtube.com/watch?v=UOxkGD8qRB4' 
	      	width = {opts.width} 
	      	height={opts.height}
	      	playing = {this.state.playing}
	      	onProgress = {this.onProgress}
	      	onDuration = {this.onDuration}
	      	onSeek = { e => console.log('onSeek',e)}

	      	/>

	      <h1>{this.state.duration}</h1>
	      <h1>{this.state.percentage}</h1>
	      <PlayerControls  seeks = {this.seekChange} duration={this.state.duration} seek={ this.state.seek } /> 
	     <button onClick = {this.onTogglePlay} >Pause/Play</button>
	      </div>
	    );
	  }

}
